package ch02.sec02;

public class LiterExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int var1 = 0b1011;
		int var2 = 0206;
		int var3 = 365;
		int var4 = 0xB3;
		
		//컨트롤 + D = 라인 삭제
		//컨트롤 + alt + 방향키 = 라인복사
		System.out.println(var1);
		System.out.println(var2);
		System.out.println(var3);
		System.out.println(var4);
		
		
	}

}
